SELECT account, name, term, billcontact, is_active, is_suspended, is_vatreg, vatnumber, updated from accountdetail, (SELECT contact as billcontact from contactdetail c where account=account) c where id IN (SELECT MAX(id) FROM accountdetail GROUP BY account) INTO OUTFILE '/tmp/organisations.csv' FIELDS TERMINATED BY ',' OPTIONALLY ENCLOSED BY '"' ESCAPED BY '\\' LINES TERMINATED BY '\n';
